        }
        else if (strcmp(buffer, "exit") == 0) {
            break;
        }
        else {
            std::cout << "Invalid command" << std::endl;
        }
    }

    // Close sockets
    closesocket(clientSocket);
    closesocket(serverSocket);

    // Cleanup Winsock
    WSACleanup();

    std::cout << "Server stopped" << std::endl;

    return 0;
