```cpp
// Server.cpp
#include <iostream>
#include <cstring>
#include <sys/socket.h>
#include <arpa/inet.h>
#include <unistd.h>

int main()
{
    int serverSocket, clientSocket;
    struct sockaddr_in serverAddress, clientAddress;
    char buffer[1024];

    // Create TCP socket
    serverSocket = socket(AF_INET, SOCK_STREAM, 0);
    if (serverSocket == -1)
    {
        std::cerr << "Failed to create socket" << std::endl;
        return 1;
    }

    // Set server address and port
    serverAddress.sin_family = AF_INET;
    serverAddress.sin_addr.s_addr = INADDR_ANY;
    serverAddress.sin_port = htons(8888);

    // Bind the socket to the server address
    if (bind(serverSocket, (struct sockaddr *)&serverAddress, sizeof(serverAddress)) < 0)
    {
        std::cerr << "Failed to bind socket" << std::endl;
        return 1;
    }

    // Mark the socket to listen for incoming connections
    listen(serverSocket, 3);
    std::cout << "Server is listening..." << std::endl;

    socklen_t clientAddressLength = sizeof(clientAddress);

    // Accept incoming connection
    clientSocket = accept(serverSocket, (struct sockaddr *)&clientAddress, &clientAddressLength);
    if (clientSocket < 0)
    {
        std::cerr << "Failed to accept connection" << std::endl;
        return 1;
    }
    std::cout << "Connection accepted from " << inet_ntoa(clientAddress.sin_addr) << std::endl;

    // Receive and print client message
    memset(buffer, 0, sizeof(buffer));
    if (read(clientSocket, buffer, sizeof(buffer)) < 0)
    {
        std::cerr << "Failed to receive message" << std::endl;
    }
    std::cout << "Received message from client: " << buffer << std::endl;

    // Close the sockets
    close(clientSocket);
    close(serverSocket);

    return 0;
}
