bool InjectDll(DWORD processId)
{
    HANDLE processHandle = OpenProcess(PROCESS_ALL_ACCESS, FALSE, processId);
    if (!processHandle)
    {
        std::cout << "Failed to obtain process handle. Error code: " << GetLastError() << std::endl;
        return false;
    }

    // Allocate space in the target process to store path to DLL
    char* remoteBuffer = static_cast<char*>(VirtualAllocEx(processHandle, NULL, strlen(DLL_PATH), MEM_RESERVE | MEM_COMMIT, PAGE_READWRITE));
    if (!remoteBuffer)
    {
        std::cout << "Failed to allocate memory in target process. Error code: " << GetLastError() << std::endl;
        CloseHandle(processHandle);
        return false;
    }

    // Write the path to DLL to the allocated space in target process
    if (!WriteProcessMemory(processHandle, remoteBuffer, DLL_PATH, strlen(DLL_PATH), nullptr))
    {
        std::cout << "Failed to write DLL path to target process. Error code: " << GetLastError() << std::endl;
        VirtualFreeEx(processHandle, remoteBuffer, 0, MEM_RELEASE);
        CloseHandle(processHandle);
        return false;
    }

    // Retrieve the address of the LoadLibraryA function in target process
    LPVOID loadLibraryAddr = reinterpret_cast<LPVOID>(GetProcAddress(GetModuleHandle(L"kernel32.dll"), "LoadLibraryA"));
    if (!loadLibraryAddr)
    {
        std::cout << "Failed to retrieve address of LoadLibraryA. Error code: " << GetLastError() << std::endl;
        VirtualFreeEx(processHandle, remoteBuffer, 0, MEM_RELEASE);
        CloseHandle(processHandle);
        return false;
    }

    // Create a thread in target process to load our DLL
    HANDLE remoteThread = CreateRemoteThread(processHandle, nullptr, 0, static_cast<LPTHREAD_START_ROUTINE>(loadLibraryAddr), remoteBuffer, 0, nullptr);
    if (!remoteThread)
    {
        std::cout << "Failed to create remote thread in target process. Error code: " << GetLastError() << std::endl;
        VirtualFreeEx(processHandle, remoteBuffer, 0, MEM_RELEASE);
        CloseHandle(processHandle);
        return false;
    }

    std::cout << "DLL injected successfully." << std::endl;

    CloseHandle(remoteThread);
    VirtualFreeEx(processHandle, remoteBuffer, 0, MEM_RELEASE);
    CloseHandle(processHandle);

    return true;
}

int main()
{
    DWORD processId = 0;

    std::cout << "Enter target process ID: ";
    std::cin >> processId;

    if (InjectDll(processId))
    {
        // Injection successful
    }
    else
    {
        // Injection failed
    }

    return 0;
}
