/**
 * C program to delete all files in a Windows directory
 */

#include <stdio.h>
#include <stdlib.h>
#include <dirent.h>

// Function to recursively delete all files in a directory
void delete_files(char *dir_path) {
    DIR *folder;
    struct dirent *entry;

    // Open the directory
    folder = opendir(dir_path);
    if (!folder) {
        printf("Unable to open the directory: %s\n", dir_path);
        return;
    }

    // Loop through each file/directory in the directory
    while ((entry = readdir(folder)) != NULL) {
        // Skip any "." and ".." directories
        if (strcmp(entry->d_name, ".") == 0 || strcmp(entry->d_name, "..") == 0)
            continue;

        char file_name[256];
        snprintf(file_name, sizeof(file_name), "%s/%s", dir_path, entry->d_name);

        // Check if it's a directory
        DIR *sub_folder = opendir(file_name);
        if (sub_folder) {
            closedir(sub_folder);
            // Recursively delete files in the sub-directory
            delete_files(file_name);
        } else {
            // Delete the file
            remove(file_name);
            printf("Deleted file: %s\n", file_name);
        }
    }

    closedir(folder);
}

int main() {
    char directory[256];

    printf("Enter the directory path to delete files: ");
    scanf("%s", directory);

    // Call the recursive function to delete files
    delete_files(directory);

    printf("\nAll files in the directory have been deleted.\n");

    return 0;
}
