#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <windows.h>

// Function to delete all files in a folder
void deleteFilesInFolder(const char* folderPath) {
    WIN32_FIND_DATAA findData;

    // Create search pattern to find files '*.*' within the given folder
    char searchPath[MAX_PATH];
    strcpy(searchPath, folderPath);
    strcat(searchPath, "\\*.*");

    HANDLE hFindFile = FindFirstFileA(searchPath, &findData);
    if (hFindFile == INVALID_HANDLE_VALUE) {
        printf("Failed to find files in the given folder: %s\n", folderPath);
        return;
    }

	// Iterate over every file in the folder
    do {
        // Exclude subdirectories by checking the file type
        if (!(findData.dwFileAttributes & FILE_ATTRIBUTE_DIRECTORY)) {
            // Construct the full path of the file
            char filePath[MAX_PATH];
            strcpy(filePath, folderPath);
            strcat(filePath, "\\");
            strcat(filePath, findData.cFileName);

            // Delete the file
            if (DeleteFileA(filePath) == 0) {
                printf("Failed to delete the file: %s\n", filePath);
            }
            else {
                printf("Deleted the file: %s\n", filePath);
            }
        }
    } while (FindNextFileA(hFindFile, &findData));

    // Check if 'FindNextFile' found a matching file, an error or the end of the directory
    DWORD lastError = GetLastError();
    if (lastError != ERROR_NO_MORE_FILES) {
        printf("Failed to iterate through files in the folder: %lu\n", lastError);
    }

    // Close the file handle to release system resources
    FindClose(hFindFile);
}

int main(void) {
    const char* folderPath = "C:\\path\\to\\folder";
    deleteFilesInFolder(folderPath);
    return 0;
}
```

