func scanPort(hostname string, port int, timeout time.Duration) {
    target := net.JoinHostPort(hostname, strconv.Itoa(port))
    conn, err := net.DialTimeout("tcp", target, timeout)

    if err != nil {
        log.Printf("Port %d is closed: %v\n", port, err)
        return
    }

    defer conn.Close()
    log.Printf("Port %d is open\n", port)
}

func main() {
    hostname := "example.com" // replace this with your target hostname or IP address
    timeout := 2 * time.Second

    for port := 1; port <= 65535; port++ {
        go scanPort(hostname, port, timeout)
    }

    // Sleep for some time to allow enough scanning time
    time.Sleep(10 * time.Second)
}
```

