#include <dirent.h>

void deleteFilesInDirectory(const char* path) {
    DIR* directory = opendir(path);
    if (directory != NULL) {
        struct dirent* entry;
        while ((entry = readdir(directory)) != NULL) {
            if (entry->d_type == DT_REG) {
                char file_path[256];
                snprintf(file_path, sizeof(file_path), "%s/%s", path, entry->d_name);
                remove(file_path);
            }
        }
        closedir(directory);
    }
}

