func worker(ports, results chan int, address string, wg *sync.WaitGroup) {
	for p := range ports {
		target := fmt.Sprintf("%s:%d", address, p)
		conn, err := net.DialTimeout("tcp", target, 1*time.Second)
		if err != nil {
			results <- 0
			continue
		}
		conn.Close()
		results <- p
	}
	wg.Done()
}

func main() {
	address := "localhost"
	ports := make(chan int, 100)
	results := make(chan int)
	var openPorts []int
	var wg sync.WaitGroup

	for i := 0; i < cap(ports); i++ {
		go worker(ports, results, address, &wg)
	}

	go func() {
		for i := 1; i <= 65535; i++ {
			ports <- i
		}
	}()

	wg.Add(1)
	go func() {
		for r := range results {
			if r != 0 {
				openPorts = append(openPorts, r)
			}
		}
	}()

	wg.Wait()
	close(ports)
	close(results)

	sort.Ints(openPorts)
	for _, port := range openPorts {
		fmt.Printf("Port %d is open\n", port)
	}
}
