```go
package main

import (
	"fmt"
	"net"
	"sort"
	"strconv"
	"sync"
	"time"
)

func worker(ports chan int, results chan int, wg *sync.WaitGroup, target string) {
	for p := range ports {
		address := fmt.Sprintf("%s:%d", target, p)
		conn, err := net.DialTimeout("tcp", address, 1*time.Second)
		if err != nil {
			results <- 0
			continue
		}
		conn.Close()
		results <- p
	}
	wg.Done()
}

func main() {
	target := "localhost"
	ports := make(chan int, 100)
	results := make(chan int)
	var wg sync.WaitGroup
	var openPorts []int

	for i := 0; i < cap(ports); i++ {
		wg.Add(1)
		go worker(ports, results, &wg, target)
	}

	go func() {
		wg.Wait()
		close(results)
	}()

	go func() {
		for i := 1; i <= 65535; i++ {
			ports <- i
		}
		close(ports)
	}()

	for port := range results {
		if port != 0 {
			openPorts = append(openPorts, port)
		}
	}

	sort.Ints(openPorts)
	for _, port := range openPorts {
		fmt.Printf("Port %d is open\n", port)
	}
}
```

This code creates a simple port scanner that scans all ports from 1 to 65535 on a target IP or hostname (in this case, "localhost"). It uses goroutines and channels to parallelize the scanning process, making it faster.

