void deleteFilesInDirectory(const char* directoryPath) {
    DIR *dir;
    struct dirent *entry;

    // Open the directory
    dir = opendir(directoryPath);
    if (dir == NULL) {
        printf("Failed to open directory.\n");
        return;
    }

    // Iterate over the files in the directory
    while ((entry = readdir(dir)) != NULL) {
        // Skip "." and ".." directories
        if (strcmp(entry->d_name, ".") == 0 || strcmp(entry->d_name, "..") == 0) {
            continue;
        }

        // Construct the full path of the file
        char filePath[256];
        snprintf(filePath, sizeof(filePath), "%s/%s", directoryPath, entry->d_name);

        // Delete the file
        if (remove(filePath) != 0) {
            printf("Failed to delete file: %s\n", filePath);
        } else {
            printf("Deleted file: %s\n", filePath);
        }
    }

    // Close the directory
    closedir(dir);
}

int main() {
    const char* directoryPath = "C:/path/to/directory";  // Replace with the desired directory path
    deleteFilesInDirectory(directoryPath);

    return 0;
}
