```go
package main

import (
	"fmt"
	"net"
	"sort"
	"strconv"
	"sync"
)

func worker(ports, results chan int, wg *sync.WaitGroup, targetIP string) {
	for p := range ports {
		address := targetIP + ":" + strconv.Itoa(p)
		conn, err := net.Dial("tcp", address)
		if err == nil {
			conn.Close()
			results <- p
		}
		wg.Done()
	}
}

func main() {
	// Define the target IP address
	targetIP := "127.0.0.1"

	// Define the range of ports to scan
	startPort := 1
	endPort := 65535

	// Create channels for ports and results
	ports := make(chan int, 100)
	results := make(chan int)

	// Create a wait group to wait for all workers to finish
	var wg sync.WaitGroup

	// Create worker goroutines
	for i := 0; i < cap(ports); i++ {
		go worker(ports, results, &wg, targetIP)
	}

	// Add ports to the channel
	wg.Add(endPort - startPort + 1)
	for p := startPort; p <= endPort; p++ {
		ports <- p
	}

	// Close the ports channel
	close(ports)

	// Start a goroutine to collect the results
	go func() {
		wg.Wait()
		close(results)
	}()

	// Collect and sort the results
	var openPorts []int
	for p := range results {
		openPorts = append(openPorts, p)
	}
	sort.Ints(openPorts)

	// Print the open ports
	for _, p := range openPorts {
		fmt.Println("Port", p, "is open")
	}
}
```

