    SOCKET serverSocket = socket(AF_INET, SOCK_STREAM, 0);
    if (serverSocket == INVALID_SOCKET)
    {
        std::cout << "Failed to create socket." << std::endl;
        WSACleanup();
        return -1;
    }

    // Bind the socket to a specific IP and port
    sockaddr_in serverAddress;
    serverAddress.sin_family = AF_INET;
    serverAddress.sin_addr.s_addr = INADDR_ANY;   // Use any available network interface
    serverAddress.sin_port = htons(12345);        // Use a specific port, e.g., 12345

    if (bind(serverSocket, (struct sockaddr*)&serverAddress, sizeof(serverAddress)) == SOCKET_ERROR)
    {
        std::cout << "Failed to bind socket." << std::endl;
        closesocket(serverSocket);
        WSACleanup();
        return -1;
    }

    // Listen for incoming connections
    if (listen(serverSocket, SOMAXCONN) != 0)
    {
        std::cout << "Failed to listen." << std::endl;
        closesocket(serverSocket);
        WSACleanup();
        return -1;
    }

    std::cout << "Waiting for incoming connections..." << std::endl;

    // Accept incoming connections
    SOCKET clientSocket;
    sockaddr_in clientAddress;
    int clientAddressSize = sizeof(clientAddress);

    while ((clientSocket = accept(serverSocket, (struct sockaddr*)&clientAddress, &clientAddressSize)) != INVALID_SOCKET)
    {
        std::cout << "New connection accepted." << std::endl;

        // Receive data from the client
        char buffer[1024];
        int bytesRead = recv(clientSocket, buffer, sizeof(buffer), 0);
        if (bytesRead > 0)
        {
            buffer[bytesRead] = '\0';
            std::cout << "Received message: " << buffer << std::endl;

            // Process the received message
            std::string response = "Server received your message: ";
            response += buffer;

            // Send a response back to the client
            send(clientSocket, response.c_str(), response.length(), 0);
            std::cout << "Response sent." << std::endl;
        }

        // Close the client socket
        closesocket(clientSocket);
    }

    // Close the server socket
    closesocket(serverSocket);

    // Cleanup Winsock
    WSACleanup();

    return 0;
}
```

