		fmt.Println("Error resolving IP address:", err)
		return
	}

	fmt.Printf("Scanning ports for: %s\n", ipAddr.String())

	startPort := 1
	endPort := 1024

	openPorts := make(chan int, 100)
	var openPortList []int

	for i := 0; i < cap(openPorts); i++ {
		go worker(ipAddr.String(), openPorts)
	}

	for port := startPort; port <= endPort; port++ {
		openPorts <- port
	}

	time.Sleep(2 * time.Second)
	close(openPorts)

	for openPort := range openPorts {
		openPortList = append(openPortList, openPort)
	}

	sort.Ints(openPortList)

	for _, port := range openPortList {
		fmt.Printf("Port %d is open\n", port)
	}
}

func worker(ipAddr string, openPorts chan int) {
	for port := range openPorts {
		address := fmt.Sprintf("%s:%d", ipAddr, port)
		conn, err := net.DialTimeout("tcp", address, 1*time.Second)
		if err == nil {
			conn.Close()
			fmt.Printf("Port %d is open\n", port)
		}
	}
}
