	// Define the target host and the range of ports to scan
	target := "localhost"
	startPort := 1
	endPort := 65535

	// Create a channel to store open ports
	openPorts := make(chan int, 100)

	// Create a channel to signal when scanning is complete
	done := make(chan bool)

	// Start a goroutine to listen for open ports
	go func() {
		for port := range openPorts {
			fmt.Printf("Port %d is open\n", port)
		}
